import React from 'react'
import { connect } from 'react-redux'
import { getBasketPhonesWithCount, getTotalBasketPrice } from 'selectors'
import * as R from 'ramda'
import { Link } from 'react-router-dom'

import {
  removePhoneFromBasket,
  cleanBasket,
  checkoutBasket
} from 'action'


const Basket = ({
  phones,
  totalCount,
  removePhoneFromBasket,
  cleanBasket,
  checkoutBasket
}) => {
  // const isBasketEmpty = phones.length === 0
  const isBasketEmpty = R.isEmpty(phones)

  const renderContent = () => (
    <div>
      {isBasketEmpty && <h2>Your shopping catr is empty</h2>}

      <div className='table-responsive'>
        <table className="table-bordered table-striped table-condensed cf">
          <tbody>
            {phones.map((phone, index) => (
              <tr key={index} className='item-checout'>
                <td className="first-column-checkout">
                  <img
                    src={phone.image}
                    alt={phone.name}
                    className="img-thumbnail"
                  />
                </td>
                <td>{phone.name}</td>
                <td>${phone.price}</td>
                <td>{phone.count}</td>
                <td>
                  <span
                    className="glyphicon glyphicon-trash"
                    onClick={() => removePhoneFromBasket(phone.id)}
                  ></span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {
        // !isBasketEmpty &&
        R.not(isBasketEmpty) &&
        <div className='row'>
          <div className="pull-right total-user-checkout">
            <b>Total: </b>
            ${totalCount}
          </div>
        </div>
      }
    </div>
  )

  const renderSidebar = () => (
    <div>
      <Link
        to='/'
        className='btn btn-info'
      >
        <span className="glyphicon glyphicon-info-sign"></span>
        <span>Continue shopping!</span>
      </Link>
      {
        !isBasketEmpty &&

        <div>
          <button
            className="btn btn-danger"
            onClick={cleanBasket}
          >
            <span className="glyphicon glyphicon-trash"></span>
            Clean basket
          </button>
          <button
            className="btn btn-success"
            onClick={() => checkoutBasket(phones)}
          >
            <span className="glyphicon glyphicon-envelope"></span>
            Checkout
          </button>
        </div>
      }
    </div>
  )

  return (
    <div className='view-container'>
      <div className="container">
        <div className="row">
          <div className="col-md-9">
            {renderContent()}
          </div>
          <div className="col-md-3 btn-user-checkout">
            {renderSidebar()}
          </div>
        </div>
      </div>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    phones: getBasketPhonesWithCount(state),
    totalCount: getTotalBasketPrice(state)
  }
}

const mapDispatchToProps = {
  removePhoneFromBasket,
  cleanBasket,
  checkoutBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Basket)