import * as R from 'ramda'

const initialState = {
  id: null,
  loading: true
}

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case 'FETCH_PHONE_BY_ID_REQUEST':
      return {
        ...state,
        loading: true
      }
    case 'FETCH_PHONE_BY_ID_SUCCESS':
      return R.merge(state, {
        id: R.prop('id', payload),
        loading: false
      })
      // return { ids: payload.id }

    default:
      return state
  }
}